mapper.select:
SELECT A.COLUMN_ID, A.TABLE_NAME, B.COMMENTS AS TABLE_COMMENT, A.COLUMN_NAME, C.COMMENTS AS COLUMN_COMMENT, A.DATA_TYPE AS COLUMN_TYPE, A.DATA_LENGTH AS COLUMN_SIZE, A.DATA_SCALE AS COLUMN_SCALE, A.DATA_DEFAULT AS DEFAULT_VALUE, CASE WHEN D.COLUMN_NAME IS NOT NULL THEN '1' ELSE '0' END AS PRIMARY_KEY_FLAG, CASE WHEN A.NULLABLE = 'N' AND D.COLUMN_NAME IS NULL THEN 'on' ELSE 'off' END AS REQUIRED_FLAG, 'on' AS SEARCH_FLAG,'on' AS LIST_FLAG,'on' AS MODIFY_FLAG,'on' AS ADD_FLAG FROM USER_TAB_COLUMNS A, USER_TAB_COMMENTS B, USER_COL_COMMENTS C, (SELECT COL.COLUMN_NAME, COL.TABLE_NAME FROM USER_CONSTRAINTS CON, USER_CONS_COLUMNS COL WHERE CON.CONSTRAINT_NAME = COL.CONSTRAINT_NAME AND CON.CONSTRAINT_TYPE = 'P') D WHERE A.TABLE_NAME = B.TABLE_NAME(+) AND A.TABLE_NAME = C.TABLE_NAME(+) AND A.COLUMN_NAME = C.COLUMN_NAME(+) AND A.TABLE_NAME = D.TABLE_NAME(+) AND A.COLUMN_NAME = D.COLUMN_NAME(+)
AND A.TABLE_NAME =#{tableName}
ORDER BY ${orderBy}
ORDER BY COLUMN_ID
